import{createUser,getUsers}from"./user.service.js";export const getUsersHandler=async(request,reply)=>{const users=await getUsers();return users};export const registerUserHandler=async(request,reply)=>{const body=request.body;try{const user=await createUser(body);return reply.status(201).send(user)}catch(error){console.error(error);reply.status(500).send({message:"Internal Server Error",error:error})}};