import crypto from"crypto";export const hashPassword=password=>{const salt=crypto.randomBytes(16).toString("hex");const hash=crypto.pbkdf2Sync(password,salt,1e3,64,"sha512").toString("hex");return{hash,salt}};export const verifyPassword=({candidatePassword,salt,hash})=>{const candidateHash=crypto.pbkdf2Sync(candidatePassword,salt,1e3,64,"sha512").toString("hex");return candidateHash===hash};